export const fetchUsers = () => {
    const transformedCollection = fetch('https://conviva-3ffbc.firebaseio.com/users.json')
        .then(response => response.json())

    return transformedCollection.reduce((acc, user) => {
        acc[user.id] = user;
        return acc;
    }, {});


    // const response = await fetch('https://conviva-3ffbc.firebaseio.com/users.json');
    // return await response.json();
    // return fetch('https://conviva-3ffbc.firebaseio.com/users.json')
    //     .then(response => response.json());

}
export const normalizeData = (data) => {
    return data.reduce( (acc, user) => {
        acc[user.id] = user;
        return acc;
    }, {});
}
