import React from 'react';
import { connect } from 'react-redux';
import { fetchUsers } from '../utils/fetch.data';

import { setCurrentUser, updateUsersList, fetchUsersStart } from '../../redux/users/user.actions';

import Loader from '../common/loader/loader.component';

import './user-list.styles.css';

class UserList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true 
        }
    }
    async componentDidMount() {
        const { updateUsersList } = this.props;
        const data = await fetchUsers();
        fetchUsersStart();
        updateUsersList(data);
        this.setState({
            isLoading: false 
        });
    }
    render() {
        const { userList, setCurrentUser } = this.props;
        return this.state.isLoading ? (
            <Loader />
        ) : (
            <div className="user-list">
                <div className="text-left">
                    <h4>Customer List</h4>
                </div>
                <table className="table">
                    <thead className="thead-dark">
                        <tr>
                            <th scope="col">Id</th>
                            <th scope="col">Name</th>
                            <th scope="col">Age</th>
                            <th scope="col">phone</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            Object.keys(userList).map(id => {
                                const user = userList[id];
                                return (
                                    <tr key={user.email} onClick={ () => setCurrentUser(user)}>
                                        <td>{user.id}</td>
                                        <td>{user.name}</td>
                                        <td>{user.age}</td>
                                        <td>{user.phone}</td>
                                    </tr>
                                );
                            })
                        }
                    </tbody>
                </table>
            </div>
        )
    }
};

const mapStateToProps = (state) => ({
    userList: state.usersData.users
})
const mapDispatchToProps = dispatch => ({
    setCurrentUser: user => dispatch(setCurrentUser(user)),
    updateUsersList: users => dispatch(updateUsersList(users)),
    fetchUsersStart: users => dispatch(fetchUsersStart())
})
export default connect(mapStateToProps, mapDispatchToProps)(UserList);
