const INITIAL_STATE = {
    users: {},
    currentUser: {}
};

const userReducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case 'SET_CURRENT_USER':
            return {
                ...state,
                currentUser: action.payload
            }
        case 'UPDATE_USER_LIST':
            return {
                ...state,
                users: action.payload
            }
        default:
            return state;
    }
};

export default userReducer;
